import React, { useState } from 'react'
import { StyleSheet, View } from 'react-native'
import { ScrollView } from 'react-native-gesture-handler'
import { PaperProvider, Text, Card, Button, TextInput } from 'react-native-paper'

const RepairCar = ({ navigation }) => {


    const [name, Setname] = useState('');
    const [number, SetNumber] = useState('');
    const [model, SetModel] = useState('');
    const [id, SetId] = useState('');
    const [service, SetServices] = useState('');
    const [date, SetDate] = useState('');

    const Send = () => {
        navigation.navigate('RegisterServices', { name: name, number: number, model: model, id: id, service: service, date: date })

    }
    return (
        <PaperProvider>
            <ScrollView style={styles.contain}>
                <Text style={styles.title}>Servicio de taller</Text>
                <Card mode='contained' style={styles.card}>
                    <Card.Cover source={{ uri: 'data:image/jpeg;base64,' }} />
                    <Card.Content>
                        <Text style={styles.Text} variant="titleLarge">Mantenimiento</Text>
                        <Text style={styles.Text} variant="bodyMedium">El mantenimiento automotriz es el conjunto de actividades que se realizan para mantener un vehículo en condiciones óptimas de funcionamiento. Esto incluye inspecciones rutinarias, revisiones programadas y la sustitución de piezas desgastadas o dañadas.
                        </Text>
                    </Card.Content>
                </Card>

                <Card mode='contained' style={styles.card}>
                    <Card.Cover source={{ uri: 'data:image/jpeg;base64,' }} />
                    <Card.Content>
                        <Text style={styles.Text} variant="titleLarge">Reparacion</Text>
                        <Text style={styles.Text} variant="bodyMedium">Los talleres de reparación de vehículos son establecimientos industriales que realizan operaciones para restablecer las condiciones normales del estado de funcionamiento del vehículo o de equipos y componentes del mismo.</Text>
                    </Card.Content>
                </Card>

                <Card mode='contained' style={styles.card}>
                    <Card.Cover source={{ uri: 'data:image/jpeg;base64,' }} />
                    <Card.Content>
                        <Text style={styles.Text} variant="titleLarge">Revisión Técnica</Text>
                        <Text style={styles.Text} variant="bodyMedium">La revisión técnica vehicular es un mantenimiento legal preventivo que evalúa las condiciones mecánicas, ambientales y de seguridad de un vehículo. Se realiza periódicamente por un ente certificador que verifica el cumplimiento de las normas de seguridad y emisiones contaminantes.</Text>
                    </Card.Content>
                </Card>

                <Text style={styles.title}>Agenda tu cita</Text>

                <View>
                    <Text style={styles.TextForm}>Nombre completo</Text>
                    <TextInput value={name} onChangeText={Setname} label={"Ingrese el nombre"} />
                    <Text style={styles.TextForm}>Numero Documento</Text>
                    <TextInput value={number} onChangeText={SetNumber} label={"Ingrese su Numero de Documento"} />
                    <Text style={styles.TextForm}>Modelo del Vehiculo</Text>
                    <TextInput value={model} onChangeText={SetModel} label={"Ingrese el modelo del vehiculo"} />
                    <Text style={styles.TextForm}>Placa Vehiculo</Text>
                    <TextInput value={id} onChangeText={SetId} label={"Ingrese la Placa del vehiculo"} />
                    <Text style={styles.TextForm}>Servicio</Text>
                    <TextInput value={service} onChangeText={SetServices} label={"Ingrese el servicio que desea"} />
                    <Text style={styles.TextForm}>Fecha</Text>
                    <TextInput value={date} onChangeText={SetDate} label={"DD/MM/AAAA : HH:hh"} />
                    <Button style={{ marginVertical: 20 }} mode="contained" onPress={Send}>Enviar solicitud</Button>
                </View>
            </ScrollView>
        </PaperProvider>
    )
}
const styles = StyleSheet.create({
    title: {
        backgroundColor: '#7F62F0',
        textAlign: 'center',
        fontSize: 30,
        fontWeight: 'bold',
        color: 'black',
        marginVertical: 30
    },
    card: {
        marginVertical: 20,
        marginHorizontal: 20,
        textAlign: 'center'
    },
    contain: {
        flex: 1
    },
    Text: {
        textAlign: 'center',
        marginVertical: 5
    },
    TextForm: {
        textAlign: 'center',
        marginVertical: 5,
        fontSize: 20,
        fontWeight: 'bold'
    }
})
export default RepairCar